$_MODULE['<{ntbackupandrestore}prestashop>faq_41906c69303e903c986fde8f7cb34665'] = 'The backup ended with an error that indicate there is not enough space for the backup, but I know there is. Why am I having this error?';
$_MODULE['<{ntbackupandrestore}prestashop>faq_8bdbb9087cc2864414759889f6d22307'] = 'This error can be caused by a limit of your server. Some server (32 bits) will not allow the backup to be of more than 2 GB. In this case, you can use the advanced option to limit backup size. Your backup will be cut in smaller parts that will be accepted by your server. For example, you can choose 1500 as the size limit for your backup file size. Pay attention that all parts are mandatory if you want to restore your shop.';
$_MODULE['<{ntbackupandrestore}prestashop>faq_afde1c7273735610102d9f166386b91e'] = 'Where can I find the backup on my server?';
$_MODULE['<{ntbackupandrestore}prestashop>faq_97058428a2f40e403df9ea773ec4ad6d'] = 'You can find your backup in the directory modules/ntbackupandrestore/backup/';
$_MODULE['<{ntbackupandrestore}prestashop>faq_6162f1ad35f8d68d2ad18e65d6af12ca'] = 'Where can I find the database dump in my backup?';
$_MODULE['<{ntbackupandrestore}prestashop>faq_259f134b1c3cb0f4211d27812fb4f339'] = 'When you create a complete backup, your database is saved with all other files in the backup file. If you want to check, untar your backup file on your computer, you can find the dump.sql in the directory modules/ntbackupandrestore/backup/';
$_MODULE['<{ntbackupandrestore}prestashop>faq_92a654ecc8f654ec6377f7a48b155a4a'] = 'Does the complete backup, really contain all my files and data?';
$_MODULE['<{ntbackupandrestore}prestashop>faq_9b1606163135003f78f33d752ffb892c'] = 'The complete backup will contain an exact copy of your website. Which means all your files and data. Unless you configure it to not backup some of them';
$_MODULE['<{ntbackupandrestore}prestashop>faq_d50856eaf5567a344aafac6b6629a581'] = 'How can I check that my backup is valid?';
$_MODULE['<{ntbackupandrestore}prestashop>faq_d2a25247fc786e9f6a27212ee9aed0b8'] = 'The best way to make sure your backup is valid, is to try to restore it somewhere else. For example, on a new domain and new database or on your computer with Wamp, Mamp, Easyphp or other local server softwares.';
$_MODULE['<{ntbackupandrestore}prestashop>faq_b26bae87fdb843d75a120d12fe51ffa6'] = 'How can I restore my backup?';
$_MODULE['<{ntbackupandrestore}prestashop>faq_84d3c56657e5438d10f92c3d36c5cf8b'] = 'To restore your backup you need to:';
$_MODULE['<{ntbackupandrestore}prestashop>faq_261aa829c8bd1bd4216bf09dc76f7326'] = 'Choose where you shop will be restored. Either at a new place (new domain and new database) for a copy/move or at the root of your current shop for its replacement.';
$_MODULE['<{ntbackupandrestore}prestashop>faq_0c02578d80e75b15e3c2fafda964bfe4'] = 'Put your backup and restore script at the choosen location';
$_MODULE['<{ntbackupandrestore}prestashop>faq_967e66ec729f29106f185bf0e5c9c267'] = 'Access the restore script, using your web browser';
$_MODULE['<{ntbackupandrestore}prestashop>faq_96c158c212b69d93f4bf51a208b7c6b0'] = 'Fill in the access credentials of the database where data will be restored.';
$_MODULE['<{ntbackupandrestore}prestashop>faq_3438a63edfc1456e8bff9b8a75f0f8c2'] = 'Optional: if needed, configure the advanced options';
$_MODULE['<{ntbackupandrestore}prestashop>faq_0b20e24f98a55bf66d6fe76363284591'] = 'Launch the restoration. Pay attention that files from your backup will overwrite potential existing files and database will be replaced by database from your backup.';
$_MODULE['<{ntbackupandrestore}prestashop>faq_f9c6fc0bb026bba923a10bfe3ddfdfd9'] = 'I do not have enough space on my server for the backup, can I directly send it on one of my distant account (Dropbox, Google Drive...)?';
$_MODULE['<{ntbackupandrestore}prestashop>faq_e90b4c705ffae4ec9f27abfa38a8b96d'] = 'Before it can be sent away, the backup need to be created on your server. You need to have at least enough space for one backup. You can choose in the advanced configuration the option that allow the automatic deletion of the backup if it has been sent to a distant account';
$_MODULE['<{ntbackupandrestore}prestashop>faq_6510898b2f7a8fa6fe8aeafb9ea5ca4d'] = 'To restore my shop, do I need to install an empty Prestashop, first?';
$_MODULE['<{ntbackupandrestore}prestashop>faq_2f8b950c8cb11678dd68bd1050f15034'] = 'You do not need to install an empty Prestashop to restore. You only need your backup file and restoration script.';
